import React from "react";
import styled from "styled-components";

function Course({ course }) {
  return (
    /*     <Container>
      <img src={course.url} alt="img_course" />
      <Title>{course.title}</Title>
      <LinkTo
        href={`https://www.youtube.com/watch?v=${course.id_video}`}
        rel="noreferrer"
        target="_blank"
      >
        Xem chi tiết
      </LinkTo>
    </Container> */
    <div class="job-title">
      <div class="top">
        <img src={course.url} alt="img" />
        <i class="fa fa-ellipsis-h" aria-hidden="true"></i>
      </div>
      <div class="rolename">
        <span>{course.title}</span>
      </div>
      <div class="description">{course.desc}</div>
      <div class="buttons">
        <a
          href={`https://www.youtube.com/watch?v=${course.id_video}`}
          rel="noreferrer"
          target="_blank"
          class="button apply-now"
        >
          Xem chi tiết
        </a>
      </div>
    </div>
  );
}

export default Course;
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  border: 1px solid #ccc;
  img {
    padding: 10px;
    width: 100px;
    height: 100px;
    object-fit: cover;
    border-radius: 4px;
  }
  @media (max-width: 489px) {
    width: 100%;
  }
`;
const Title = styled.p`
  font-weight: 600;
  text-align: center;
  font-size: 20px;
  padding: 10px 0;
`;
const LinkTo = styled.a`
  background-color: #3163f7;
  color: white;
  padding: 10px;
  border-radius: 4px;
  margin-top: 10px;
`;
