import React, { useState, useEffect } from "react";
import TextField from "@material-ui/core/TextField";
import styled from "styled-components";
import { signup } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";

function SignUpScreen(props) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [fullname, setFullname] = useState("");
  const [password, setPassword] = useState("");
  const [avartar, setAvartar] = useState();
  const [repassword, setRepassword] = useState("");
  const userSignup = useSelector((state) => state.userSignup);
  const { loading, success, error } = userSignup;
  const dispatch = useDispatch();
  useEffect(() => {
    if (success) {
      props.history.push("/signin");
    }
    if (error) {
      alert("Lỗi khi đăng ký tài khoản vui lòng thử lại");
    }
  }, [success]);
  const handleSubmit = (e) => {
    e.preventDefault();
    if (password === repassword) {
      const user = new FormData();
      user.append("username", username);
      user.append("password", password);
      user.append("email", email);
      user.append("full_name", fullname);
      user.append("confirmpassword", repassword);
      user.append("avatar", avartar, avartar.name);
      dispatch(signup(user));
    } else {
      alert("Mật khẩu không khớp");
    }
  };
  return (
    <Container>
      <Left></Left>
      <Right>
        <Title>Đăng ký</Title>
        <Form onSubmit={handleSubmit}>
          <TextField
            id="username"
            label="Tài khoản"
            placeholder="..."
            type="text"
            fullWidth
            onChange={(e) => {
              setUsername(e.target.value);
            }}
          />
          <TextField
            id="email"
            label="Email"
            placeholder="..."
            type="text"
            fullWidth
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
          <TextField
            id="fullname"
            label="Full name"
            placeholder="..."
            type="text"
            fullWidth
            onChange={(e) => {
              setFullname(e.target.value);
            }}
          />
          <TextField
            id="avartar"
            type="file"
            fullWidth
            onChange={(e) => {
              setAvartar(e.target.files[0]);
            }}
          />
          <TextField
            id="password"
            label="Mật khẩu"
            placeholder="..."
            fullWidth
            type="password"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
          <TextField
            id="repassword"
            label="Xác nhận mật khẩu"
            placeholder="..."
            type="password"
            fullWidth
            onChange={(e) => {
              setRepassword(e.target.value);
            }}
          />
          <Button type="submit">Đăng ký</Button>
        </Form>
      </Right>
    </Container>
  );
}

export default SignUpScreen;
const Container = styled.div`
  margin-top: 100px;
  padding: 20px;
  display: flex;
  align-items: center;
`;
const Left = styled.div`
  flex: 1;
  background-color: orange;
`;
const Right = styled.div`
  flex: 2;
`;
const Title = styled.h2``;
const Form = styled.form``;
const Button = styled.button``;
