import "./user.css";
import { useState, useEffect } from "react";
import Error404Page from "./Error404Page";
import { useSelector, useDispatch } from "react-redux";
import AdminOption from "../components/admin/AdminOption";
import Button from "@material-ui/core/Button";
import { useHistory } from "react-router-dom";
import axios from "axios";
import Cookie from "js-cookie";
import { signout } from "../actions/userActions";
function UpdateUserScreen(props) {
  const userSignin = useSelector((state) => state.userSignin);
  const { loadinguser, userInfo, error } = userSignin;
  const [loading, setLoading] = useState(false);
  const [course, setCourse] = useState();
  const [url, setUrl] = useState("");
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [source, setSource] = useState("");
  const [img, setImg] = useState("");
  const history = useHistory();
  const dispatch = useDispatch();
  const updatecourse = async () => {
    const data = {
      id: props.match.params.id,
      title: title,
      id_video: url,
      description: desc,
      src: source,
      url: img,
    };
    await axios
      .put("/course/", data)
      .then((res) => {
        if (res.status === 200) {
          alert("Cập nhật khóa học thành công");
          history.push("/admin/courses");
        }
      })
      .catch((error) => {
        alert(error);
      });
  };
  useEffect(() => {
    axios
      .get("/account/check-login/", {
        headers: { Authorization: "Bearer " + Cookie.get("access_token") },
      })
      .then((res) => {
        if (res.status === 200) {
          axios
            .get("/course/")
            .then((res) => {
              if (res.status === 200) {
                setCourse(
                  res.data.find((x) => x.id === parseInt(props.match.params.id))
                );
                setLoading(true);
              }
            })
            .catch((error) => {
              alert(error);
            });
          if (loading) {
            setUrl(course.id_video);
            setTitle(course.title);
            setDesc(course.description);
            setSource(course.src);
            setImg(course.url);
          }
        }
      })
      .catch((error) => {
        dispatch(signout());
      });
  }, [loading]);
  return (
    <>
      {userInfo?.is_staff ? (
        <div className="user">
          <div className="userContainer">
            <div className="usershow">
              <AdminOption />
            </div>
            <div className="userUpdate">
              <span className="userUpdateTitle">Edit Course</span>
              <form className="userUpdateForm">
                <div className="userUpdateLeft">
                  <div className="userUpdateItem">
                    <label>Tên khóa học</label>
                    <input
                      type="text"
                      value={title}
                      className="userUpdateInput"
                      onChange={(e) => setTitle(e.target.value)}
                    />
                  </div>
                  <div className="userUpdateItem">
                    <label>ID video</label>
                    <input
                      type="text"
                      value={url}
                      className="userUpdateInput"
                      onChange={(e) => setUrl(e.target.value)}
                    />
                  </div>
                  <div className="userUpdateItem">
                    <label>Mô tả khóa học</label>
                    <input
                      type="text"
                      className="userUpdateInput"
                      value={desc}
                      onChange={(e) => setDesc(e.target.value)}
                    />
                  </div>
                  <div className="userUpdateItem">
                    <label>Url ảnh khóa học</label>
                    <input
                      type="text"
                      value={img}
                      className="col-lg-6"
                      onChange={(e) => setImg(e.target.value)}
                      className="userUpdateInput"
                    />
                  </div>
                  <div className="userUpdateItem">
                    <label>Nguồn khóa học</label>
                    <input
                      type="text"
                      placeholder="VietNam"
                      className="userUpdateInput"
                    />
                  </div>
                </div>
                <div className="userUpdateRight">
                  <div className="userUpdateUpload">
                    <img
                      src="https://www.pngkey.com/png/detail/114-1149878_setting-user-avatar-in-specific-size-without-breaking.png"
                      alt="img"
                      className="userUpdateImg"
                    />
                  </div>
                  <button class="userUpdateButton">Update</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      ) : (
        <Error404Page />
      )}
    </>
  );
}
export default UpdateUserScreen;
