import React, { useState } from "react";
import styled from "styled-components";
import MenuIcon from "@material-ui/icons/Menu";
import CloseIcon from "@material-ui/icons/Close";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import HomeIcon from "@material-ui/icons/Home";
import ForumIcon from "@material-ui/icons/Forum";
import MenuBookIcon from "@material-ui/icons/MenuBook";
import { signout } from "../actions/userActions";
import SupervisorAccountIcon from "@material-ui/icons/SupervisorAccount";
import { useHistory } from "react-router-dom";

function Header() {
  const [burgerState, setBurgerState] = useState(false);
  const [openSubMenu, setopenSubMenu] = useState(false);
  const dispatch = useDispatch();
  const history = useHistory();
  const userSignin = useSelector((state) => state.userSignin);
  const { loading, userInfo, error } = userSignin;
  const [info, setInfo] = useState(userInfo);
  const signOut = async () => {
    await dispatch(signout());
    setInfo();
    setopenSubMenu(false);
    history.push('/signin');
  };
  const toogleSubMenu = () => {
    setopenSubMenu(!openSubMenu);
  };
  return (
    <Content>
      <Container>
        <a>
          <img src="/images/logo.png" alt="logo" />
        </a>
        <Menu>
          <MenuItemContainer>
            <Link to="/">
              <p>Trang Chủ</p>
            </Link>
          </MenuItemContainer>
          <MenuItemContainer>
            <Link to="/course">
              <p>Khóa học</p>
            </Link>
          </MenuItemContainer>
          <MenuItemContainer>
            <Link to="/forum">
              <p>Tương tác</p>
            </Link>
          </MenuItemContainer>
        </Menu>
        <RightMenu>
          {info ? (
            <SelectMenu onClick={toogleSubMenu}>
              <img
                src={info.avatar}
                alt="avatar"
                style={{ borderRadius: "99px" }}
              />
              <ArrowContainer className="arrowcontainer">
                <img src="/images/arrow.png" alt="arrow" />
              </ArrowContainer>
            </SelectMenu>
          ) : (
            <Link to="/signin">
              <p>Đăng nhập</p>
            </Link>
          )}
        </RightMenu>
        <ContainerCustomeMenu>
          <CustomMenu onClick={() => setBurgerState(!burgerState)}></CustomMenu>
        </ContainerCustomeMenu>
        <BurgerNav show={burgerState}>
          <CloseWrapper>
            <CustomeClose onClick={() => setBurgerState(false)} />
          </CloseWrapper>
          <ElementMenu>
            <HomeIcon />
            <div>
              <Link to="/">Trang Chủ</Link>
            </div>
          </ElementMenu>
          <ElementMenu>
            <MenuBookIcon />
            <div>
              <Link to="/course">Khóa học</Link>
            </div>
          </ElementMenu>
          <ElementMenu>
            <ForumIcon />
            <div>
              <Link to="/forum">Tương tác</Link>
            </div>
          </ElementMenu>
          {info ? (
            <div>
              <ElementMenu>
                <ExitToAppIcon />
                <Link style={{ marginLeft: "10px" }} onClick={signOut}>
                  Đăng xuất
                </Link>
              </ElementMenu>
              {info.is_staff && (
                <ElementMenu>
                  <SupervisorAccountIcon />
                  <Link style={{ marginLeft: "10px" }} to="/admin/users">
                    Quản lý
                  </Link>
                </ElementMenu>
              )}
            </div>
          ) : (
            <div
              style={{
                display: "flex",
                alignItems: "center",
                padding: "10px 0",
              }}
            >
              <ExitToAppIcon />
              <Link style={{ marginLeft: "10px" }} to="/signin">
                Đăng nhập
              </Link>
            </div>
          )}
        </BurgerNav>
      </Container>
      {openSubMenu ? (
        <SubMenu className="sub_menu">
          <ListSubMenu>
            <ElementSubMenu>
              <ExitToAppIcon />
              <Link style={{ marginLeft: "10px" }} onClick={signOut}>
                Đăng xuất
              </Link>
            </ElementSubMenu>
            {info?.is_staff ? (
              <SubNewMenu>
                <ElementMenu>
                  <SupervisorAccountIcon />
                  <div>
                    <Link to="/admin/users">Quản lý</Link>
                  </div>
                </ElementMenu>
              </SubNewMenu>
            ) : (
              <></>
            )}
          </ListSubMenu>
        </SubMenu>
      ) : (
        <></>
      )}
    </Content>
  );
}

export default Header;
const Content = styled.div`
  width: 100%;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1;
`;
const Container = styled.div`
  min-height: 60px;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 20px;
  background-color: #6666ff;
  a img {
    width: 68px;
    height: 68px;
    border-radius: 99px;
    margin-top: 10px;
  }
`;
